import React from 'react'
import { useForm } from "react-hook-form"
import axios from 'axios';

function CreatPostForm() {
    const { register, handleSubmit,reset } = useForm()


    const onSubmit = async (data) => {
        try {
            await axios.post('http://localhost:8000/api/post/create', data);
            reset()
          } catch (error) {
            // Handle error
            console.error('Error posting data:', error);
          }
    }

  return (

         <form className="border-none" onSubmit={handleSubmit(onSubmit)}>
             <div className='flex justify-center items-center max-w-sm flex-col  bg-white border border-gray-200 rounded-lg shadow bg-gray-800 dark:border-gray-700 mt-10 mb-10'>
                <div className='m-5 text-black'>
                    Title:
                <input {...register("title")} />

                </div>
                <div className='mb-5 text-black'>
                    Description:
                <input {...register("description")} />
                </div>
                <div className='mb-5 text-black'>
                    Image: 
                <input {...register("img")} />
                </div>

                <input className='bg-teal-500 hover:bg-teal-600 text-white font-semibold  py-2 px-6   rounded' type="submit" />

            </div>
        </form>     
    )
}

export default CreatPostForm
