 import axios from 'axios';
import React, { useEffect, useState } from 'react'



 function ListOfPost() {
    const [blogs, setBlogs] = useState([]);
    const [refresh, setRefresh] = useState(false)

  useEffect(() => {
    const fetchData = async () => {
      try {
        const res = await axios.get('http://localhost:8000/api/post/get'); 
        setBlogs(res.data);
      } catch (error) {
        console.error('Error fetching data:', error);
      }
    };

    fetchData();
  }, []);

  const deletePost = async (id) => {
    try {
      const res = await axios.delete(`http://localhost:8000/api/post/delete/${id}`);
      console.log(res.data.status);
    } catch (error) {
      console.error('Error deleting data:', error);
    }
  };
   return (
    <div className='container mx-auto'>
    {
      blogs?.getAllPost?.map(blog=>(
        <div
        className="m-2 flex justify-around items-center border-solid border-2 border-sky-500 rounded-md ">
          <img
            className="rounded-t-lg object-fill h-48 w-48"
            src={blog.img}
            alt="" />


          <h5>
            Title:
            {blog.title}
          </h5>
          <p>
            Description
           {blog.description}
          </p>


        <div className='flex gap-2'>
            <button className="text-green-500">Edit</button>
            <button onClick={(e)=> deletePost(blog._id)} className="text-red-500">Delete</button>

        </div>

      </div>
      ))
    }

  </div>
   )
 }

 export default ListOfPost
