import React from 'react'
import { Link } from 'react-router-dom'
import Modal from '../Utils/Modal';

function ViewListBlog({blog, showModal, setShowModal, deletePost}) {
  return (
    <div className="m-2 flex justify-around items-center border-solid border-2 border-sky-500 rounded-md ">

          <img
            className="rounded-t-lg object-fill h-48 w-48"
            src={blog.img}
            alt="" />

          <div className='flex flex-col justify-around items-center p-2'>
            <h5>
              Title:
              {blog.title}
            </h5>

            <p>
              Description: 
            {blog.description}
            </p>
          </div> 
          <div className='flex gap-2'>
              <Link to={`update/${blog._id}`} className="bg-green-500 text-white active:bg-pink-600 font-bold uppercase text-sm px-6 py-3 rounded shadow hover:shadow-lg outline-none focus:outline-none mr-1 mb-1 ease-linear transition-all duration-150">Edit</Link>
              {/* Delete Modal */}
              <button
                  className="bg-red-500 text-white active:bg-pink-600 font-bold uppercase text-sm px-6 py-3 rounded shadow hover:shadow-lg outline-none focus:outline-none mr-1 mb-1 ease-linear transition-all duration-150"
                  type="button"
                  onClick={() => setShowModal(true)}
              >
                  Delete
              </button>
              {showModal && <Modal setShowModal={setShowModal} deletePost={deletePost} id={blog._id}/>}
          </div>
        </div>
  )
}

export default ViewListBlog
