import React from 'react'
import { useForm } from "react-hook-form"
import axios from 'axios';

function CreatPostForm() {
    const { register, handleSubmit,reset } = useForm()


    const onSubmit = async (data) => {
        try {
            await axios.post('http://localhost:8000/api/post/create', data);
            reset()
          } catch (error) {
            // Handle error
            console.error('Error posting data:', error);
          }
    }

  return (
        <div style={{width: "700px"}}>

            <form onSubmit={handleSubmit(onSubmit)}>
                <div className='flex justify-center items-center w-full flex-col  bg-white border rounded-lg shadow bg-gray-300 p-10'>
                    <div className='m-5 w-full text-black'>
                        <label className='block mb-2 text-sm font-blod text-black dark:text-black-500'>Title:</label>
                    <input className='w-full pl-3 h-10 rounded-md'  {...register("title")} placeholder="Title of the Blog"/>

                    </div>
                    <div className='mb-5 w-full text-black'>
                        <label className='block mb-2 text-sm font-blod text-black dark:text-black-500'>Description:</label>
                    <textarea className='w-full pl-3 h-40 rounded-md' {...register("description")} placeholder="Write description here..."/>
                    </div>
                    <div className='mb-5 w-full text-black'> 
                        <label className='block mb-2 text-sm font-blod text-black dark:text-black-500'>Image:</label>
                    <input className='w-full pl-3 h-10 rounded-md' {...register("img")} placeholder="image link provide here..."/>
                    </div>

                    <input className='w-full bg-teal-500 hover:bg-teal-600 text-white font-semibold rounded-md' type="submit" />

                </div>
            </form>    
        </div>
    )
}

export default CreatPostForm
