import React from 'react'
import { useForm } from "react-hook-form"
import axios from 'axios';

function CreatPostForm() {
    const { register, handleSubmit,reset } = useForm()


    const onSubmit = async (data) => {
        try {
            await axios.post('http://localhost:8000/api/post/create', data);
            reset()
          } catch (error) {
            // Handle error
            console.error('Error posting data:', error);
          }
    }

  return (
        <div style={{width: "700px", height: "400px", background: "green"}}>

            <form onSubmit={handleSubmit(onSubmit)}>
                <div className='flex justify-center items-center w-96 flex-col  bg-white border border-gray-200 rounded-lg shadow bg-gray-300 dark:border-gray-700 p-10'>
                    <div className='m-5 text-black'>
                        <label className='block mb-2 text-sm font-blod text-black dark:text-black-500'>Title:</label>
                    <input className=''  {...register("title")} />

                    </div>
                    <div className='mb-5 text-black'>
                        <label className='block mb-2 text-sm font-blod text-black dark:text-black-500'>Description:</label>
                    <input {...register("description")} />
                    </div>
                    <div className='mb-5 text-black'> 
                        <label className='block mb-2 text-sm font-blod text-black dark:text-black-500'>Image:</label>
                    <input {...register("img")} />
                    </div>

                    <input className='bg-teal-500 hover:bg-teal-600 text-white font-semibold  py-2 px-6 rounded mb-10' type="submit" />

                </div>
            </form>    
        </div>
    )
}

export default CreatPostForm
