 import axios from 'axios';
import React, { useEffect, useState } from 'react'
import { Link } from 'react-router-dom';


 function ListOfPost() {
    const [blogs, setBlogs] = useState([]);

  useEffect(() => {
    const fetchData = async () => {
      try {
        const res = await axios.get('http://localhost:8000/api/post/get'); 
        console.log(res);
        setBlogs(res.data);
      } catch (error) {
        console.error('Error fetching data:', error);
      }
    };

    fetchData();
  }, []);

   return (
    <div className='container mx-auto'>
    {
      blogs?.getAllPost?.map(blog=>(
        <div
        className="m-2 block rounded-lg shadow-[0_2px_15px_-3px_rgba(0,0,0,0.07),0_10px_20px_-2px_rgba(0,0,0,0.04)] dark:bg-neutral-700">
        <a href="#!">
