 import axios from 'axios';
import React, { useEffect, useState } from 'react'
// import ModalEdit from './ModalEdit';
import { useForm } from "react-hook-form";



 function ListOfPost() {
    const [blogs, setBlogs] = useState([]);
    const [refresh, setRefresh] = useState(false)
    const [showModal, setShowModal] = useState(false);
    const [showModalEdit, setShowModalEdit] = useState(false);
    const { register, handleSubmit } = useForm()

  useEffect(() => {
    const fetchData = async () => {
      try {
        const res = await axios.get('http://localhost:8000/api/post/get'); 
        setBlogs(res.data);
      } catch (error) {
        console.error('Error fetching data:', error);
      }
    };

    fetchData();
  }, [refresh]);

  const deletePost = async (id) => {
    try {
      const res = await axios.delete(`http://localhost:8000/api/post/delete/${id}`);
      if(res.status === 200){
          setRefresh(!refresh);
          setShowModal(false)
      }
    } catch (error) {
      console.error('Error deleting data:', error);
    }
  };

  const onSubmit = async (data) => {
    try {
        await axios.put(`http://localhost:8000/api/post/update/${id}`, data);

      } catch (error) {
        // Handle error
        console.error('Error posting data:', error);
    }
  }
   return (
    <div className='container mx-auto'>
    {
      blogs?.getAllPost?.map(blog=>(
        <div key={blog._id}
          className="m-2 flex justify-around items-center border-solid border-2 border-sky-500 rounded-md ">
          <img
            className="rounded-t-lg object-fill h-48 w-48"
            src={blog.img}
            alt="" />

          <div className='flex flex-col justify-around items-center p-2'>
            <h5>
              Title:
              {blog.title}
            </h5>

            <p>
              Description
            {blog.description}
            </p>
          </div> 
          <div className='flex gap-2'>
            {/* Edit Modal */}
              <button
                  className="bg-green-500 text-white active:bg-pink-600 font-bold uppercase text-sm px-6 py-3 rounded shadow hover:shadow-lg outline-none focus:outline-none mr-1 mb-1 ease-linear transition-all duration-150"
                  type="button"
                  onClick={() => setShowModalEdit(true)}
              >
                  Edit
              </button>
              {
                showModalEdit ? 
                // <ModalEdit setShowModalEdit={setShowModalEdit} blog={blog}/> 
                <>
                <div
                  className="justify-center items-center flex overflow-x-hidden overflow-y-auto fixed inset-0 z-50 outline-none focus:outline-none"
                >
                  <div className="relative w-auto my-2 mx-auto max-w-3xl">
                    {/*content*/}
                    <div className="border-0 rounded-lg shadow-lg relative flex flex-col w-full bg-white outline-none focus:outline-none">
                      {/*header*/}
                      <div className="text-center border-b border-solid border-blueGray-200 rounded-t">
                        <h3 className="text-2xl font-semibold ">
                          Update Blog {blog._id}
                        </h3>
                        <button
                          className="p-1 ml-auto bg-transparent border-0 text-black opacity-5 float-right text-2xl leading-none font-semibold outline-none focus:outline-none"
                          onClick={() => setShowModalEdit(false)}
                        >
                          <span className="bg-transparent text-black opacity-5 h-6 w-6 text-2xl block outline-none focus:outline-none">
                            ×
                          </span>
                        </button>
                      </div>
                      {/*body*/}
                      <div className="relative p-3 flex-auto">
                      <div style={{width: "700px"}}>
                        <form onSubmit={handleSubmit(onSubmit)}>
                            <div className='flex justify-center items-center w-full flex-col gap-2 bg-white border rounded-lg shadow bg-gray-300 p-10'>
                                <div className='m-5 w-full text-black'>
                                  <label className='block mb-2 text-sm font-blod text-black dark:text-black-500'>Title:</label>
                                  <input className='w-full pl-3 h-10 rounded-md'  {...register("title")} defaultValue={blog.title}/>
                                </div>
                                <div className='mb-5 w-full text-black'>
                                  <label className='block mb-2 text-sm font-blod text-black dark:text-black-500'>Description:</label>
                                  <textarea className='w-full pl-3 h-40 rounded-md' {...register("description")} defaultValue={blog.description}/>
                                </div>
                                <div className='mb-5 w-full text-black'> 
                                  <label className='block mb-2 text-sm font-blod text-black dark:text-black-500'>Image:</label>
                                  <input className='w-full pl-3 h-10 rounded-md' {...register("img")} defaultValue={blog.img}/>
                                </div>

                                <div className="flex items-center justify-end p-6 border-t border-solid border-blueGray-200 rounded-b">
                                  <button
                                      className="text-red-500 background-transparent font-bold uppercase px-6 py-2 text-sm outline-none focus:outline-none mr-1 mb-1 ease-linear transition-all duration-150"
                                      type="button"
                                      onClick={() => setShowModalEdit(false)}
                                    >
                                      Close
                                    </button>
                                    <input className='w-full bg-teal-500 hover:bg-teal-600 text-white font-semibold  py-2 px-6 rounded-md' type="submit" />
                                </div>
                            </div>
                        </form>    
                      </div>
                      </div>
                      {/*footer*/}

                    </div>
                  </div>
                </div>
                <div className="opacity-25 fixed inset-0 z-40 bg-black"></div>
              </>
                : null
              }
              {/* Delete Modal */}
              <button
                  className="bg-red-500 text-white active:bg-pink-600 font-bold uppercase text-sm px-6 py-3 rounded shadow hover:shadow-lg outline-none focus:outline-none mr-1 mb-1 ease-linear transition-all duration-150"
                  type="button"
                  onClick={() => setShowModal(true)}
              >
                  Delete
              </button>
              {showModal ? (
                  <>
                      <div
                          className="justify-center items-center flex overflow-x-hidden overflow-y-auto fixed inset-0 z-50 outline-none focus:outline-none"
                      >
                          <div className="relative w-auto my-6 mx-auto max-w-3xl">
                          {/*content*/}
                          <div className="border-0 rounded-lg shadow-lg relative flex flex-col w-full bg-white outline-none focus:outline-none">
                              {/*header*/}
                              <div className="flex items-start justify-between p-5 border-b border-solid border-blueGray-200 rounded-t">
                              <h3 className="text-3xl font-semibold">
                                  Want to delete?
                              </h3>
                              <button
                                  className="p-1 ml-auto bg-transparent border-0 text-black opacity-5 float-right text-3xl leading-none font-semibold outline-none focus:outline-none"
                                  onClick={() => setShowModal(false)}
                              >
                                  <span className="bg-transparent text-black opacity-5 h-6 w-6 text-2xl block outline-none focus:outline-none">
                                  ×
                                  </span>
                              </button>
                              </div>

                              {/*footer*/}
                              <div className="flex items-center justify-end p-6 border-t border-solid border-blueGray-200 rounded-b">
                              <button
                                  className="text-green-500 background-transparent font-bold uppercase px-6 py-2 text-sm outline-none focus:outline-none mr-1 mb-1 ease-linear transition-all duration-150"
                                  type="button"
                                  onClick={() => setShowModal(false)}
                              >
                                  Close
                              </button>
                              <button
                                  className="bg-red-500 text-white active:bg-emerald-600 font-bold uppercase text-sm px-6 py-3 rounded shadow hover:shadow-lg outline-none focus:outline-none mr-1 mb-1 ease-linear transition-all duration-150"
                                  type="button"
                                  onClick={() => { deletePost(blog._id)}}
                              >
                                  Delete
                              </button>
                              </div>
                          </div>
                          </div>
                      </div>
                      <div className="opacity-25 fixed inset-0 z-40 bg-black"></div>
                  </>
              ) : null}
          </div>
        </div>
      ))
    }

  </div>
   )
 }

 export default ListOfPost
