import axios from "axios";
import React from "react";
import { useForm } from "react-hook-form";

export default function EditModal({setShowModalEdit, blog, id}) {

  const { register, handleSubmit } = useForm()

console.log(id);
    const onSubmit = async (data) => {
      try {
          await axios.put(`http://localhost:8000/api/post/update/${blog._id}`, data);

        } catch (error) {
          // Handle error
          console.error('Error posting data:', error);
      }
    }
  return (
      <>
        <div
          className="justify-center items-center flex overflow-x-hidden overflow-y-auto fixed inset-0 z-50 outline-none focus:outline-none"
        >
          <div className="relative w-auto my-2 mx-auto max-w-3xl">
            {/*content*/}
            <div className="border-0 rounded-lg shadow-lg relative flex flex-col w-full bg-white outline-none focus:outline-none">
              {/*header*/}
              <div className="text-center border-b border-solid border-blueGray-200 rounded-t">
                <h3 className="text-2xl font-semibold ">
                  Update Blog
                </h3>
                <button
                  className="p-1 ml-auto bg-transparent border-0 text-black opacity-5 float-right text-2xl leading-none font-semibold outline-none focus:outline-none"
                  onClick={() => setShowModalEdit(false)}
                >
                  <span className="bg-transparent text-black opacity-5 h-6 w-6 text-2xl block outline-none focus:outline-none">
                    Ã—
                  </span>
                </button>
              </div>
              {/*body*/}
              <div className="relative p-3 flex-auto">
              <div style={{width: "700px"}}>
                <form onSubmit={handleSubmit(onSubmit)}>
                    <div className='flex justify-center items-center w-full flex-col gap-2 bg-white border rounded-lg shadow bg-gray-300 p-10'>
                        <div className='m-5 w-full text-black'>
                            <label className='block mb-2 text-sm font-blod text-black dark:text-black-500'>Title:</label>
                        <input className='w-full pl-3 h-10 rounded-md'  {...register("title")} defaultValue={blog.title}/>

                        </div>
                        <div className='mb-5 w-full text-black'>
                            <label className='block mb-2 text-sm font-blod text-black dark:text-black-500'>Description:</label>
                        <textarea className='w-full pl-3 h-40 rounded-md' {...register("description")} defaultValue={blog.description}/>
                        </div>
                        <div className='mb-5 w-full text-black'> 
                            <label className='block mb-2 text-sm font-blod text-black dark:text-black-500'>Image:</label>
                        <input className='w-full pl-3 h-10 rounded-md' {...register("img")} defaultValue={blog.img}/>
                        </div>

                        <div className="flex items-center justify-end p-6 border-t border-solid border-blueGray-200 rounded-b">
                          <button
                              className="text-red-500 background-transparent font-bold uppercase px-6 py-2 text-sm outline-none focus:outline-none mr-1 mb-1 ease-linear transition-all duration-150"
                              type="button"
                              onClick={() => setShowModalEdit(false)}
                            >
                              Close
                            </button>
                            <input className='w-full bg-teal-500 hover:bg-teal-600 text-white font-semibold  py-2 px-6 rounded-md' type="submit" />
                        </div>
                    </div>
                </form>    
              </div>
              </div>
              {/*footer*/}

            </div>
          </div>
        </div>
        <div className="opacity-25 fixed inset-0 z-40 bg-black"></div>
      </>


  );
}
