import React, { useState } from 'react'
import CreatPostForm from '../../Components/CreatPostForm'
import ListOfPost from '../../Components/ListOfPost'

function Dashboard() {
  const [createToggle, setCreateToggle] = useState(false)
  const [viewToggle, setViewToggle] = useState(false)

  return (
    <div className='container mx-auto  min-h-screen '>
        <div className=' '>
            <div className='flex justify-between'>
                <button onClick={()=> setCreateToggle(!createToggle)} className='relative flex flex-wrap items-center justify-between px-2 py-3 bg-teal-500 mb-3 text-white'>Create Post</button>
                <button onClick={()=> setViewToggle(!viewToggle)} className='relative flex flex-wrap items-center justify-between px-2 py-3 bg-teal-500 mb-3 text-white'>View Post</button>
            </div>
            <div className='flex justify-center items-center'>
              {
                viewToggle ?  <ListOfPost/> : <CreatPostForm/> 

              }
              {
                createToggle ?   <CreatPostForm/> :<ListOfPost/> 

              }
            </div>
        </div>
    </div>
  )
}

export default Dashboard
