import React, { useState } from 'react'
import CreatPostForm from '../../Components/CreatPostForm'

function Dashboard() {
  const {toggle, setToggle} = useState(false)

  const handleToggle = (e) =>{
    setToggle(!toggle)
  }
  return (
    <div className='container mx-auto '>
        <div className=' '>
            <div className='flex justify-between'>
                <button onClick={handleToggle} className='relative flex flex-wrap items-center justify-between px-2 py-3 bg-teal-500 mb-3 text-white'>Create Post</button>
                <button onClick={handleToggle} className='relative flex flex-wrap items-center justify-between px-2 py-3 bg-teal-500 mb-3 text-white'>View Post</button>

            </div>
            <div className=' border-2 border-sky-500 rounded-md m-2'>
              {
                toggle && <CreatPostForm/> 

              }
            </div>
        </div>
    </div>
  )
}

export default Dashboard
