import axios from 'axios';
import React, { useEffect, useState } from 'react';
import { Link, useParams } from 'react-router-dom';
import { TERipple } from 'tw-elements-react';


function SinglePost() {
  const [blog, setBlog] = useState({});
  const {id} = useParams()
  useEffect(() => {
    const fetchData = async () => {
      try {
        const res = await axios.get(`http://localhost:8000/api/post/get/${id}`); 
        setBlog(res.data);
      } catch (error) {
        console.error('Error fetching data:', error);
      }
    };

    fetchData();
  }, [id]);

  return (
    <div
        className="m-2 block rounded-lg shadow-[0_2px_15px_-3px_rgba(0,0,0,0.07),0_10px_20px_-2px_rgba(0,0,0,0.04)] dark:bg-neutral-700">
        <a href="#!">
          <img
            className="rounded-t-lg object-center"
            src={blog.img}
            alt="" />
        </a>
        <div className="p-6">
          <h5
            className="mb-2 text-3xl font-medium leading-tight text-neutral-800 dark:text-neutral-50 ">
            {blog.title}
          </h5>
          <p className="mb-4 text-base text-neutral-600 dark:text-neutral-200 text-justify">
            {blog.description}
          </p>
          <TERipple>
            <Link
                to={"/home"}
              type="button"
              className="bg-white hover:bg-gray-100 text-gray-800 font-semibold py-2 px-4 border border-gray-400 rounded shadow">
              Back to Home
            </Link>
          </TERipple>
        </div>
      </div>
  )
}

export default SinglePost
