 import axios from 'axios';
import React, { useEffect, useState } from 'react'
import Modal from './Modal';



 function ListOfPost() {
    const [blogs, setBlogs] = useState([]);
    const [refresh, setRefresh] = useState(false)
    const [showModal, setShowModal] = React.useState(false);

  useEffect(() => {
    const fetchData = async () => {
      try {
        const res = await axios.get('http://localhost:8000/api/post/get'); 
        setBlogs(res.data);
      } catch (error) {
        console.error('Error fetching data:', error);
      }
    };

    fetchData();
  }, [refresh]);

  const deletePost = async (id) => {
    try {
      const res = await axios.delete(`http://localhost:8000/api/post/delete/${id}`);
      if(res.status === 200){

          setRefresh(!refresh);
      }
    } catch (error) {
      console.error('Error deleting data:', error);
    }
  };
   return (
    <div className='container mx-auto'>
    {
      blogs?.getAllPost?.map(blog=>(
        <div
        className="m-2 flex justify-around items-center border-solid border-2 border-sky-500 rounded-md ">
          <img
            className="rounded-t-lg object-fill h-48 w-48"
            src={blog.img}
            alt="" />


          <h5>
            Title:
            {blog.title}
          </h5>
          <p>
            Description
           {blog.description}
          </p>


        <div className='flex gap-2'>
            <button className="text-green-500">Edit</button>
            <button
                className="bg-red-500 text-white active:bg-red-600 font-bold uppercase text-sm px-6 py-3 rounded shadow hover:shadow-lg outline-none focus:outline-none mr-1 mb-1 ease-linear transition-all duration-150"
                type="button"
                onClick={() => setShowModal(true)}
            >
                Delete
            </button>
            {/* <button onClick={(e)=> deletePost(blog._id)} className="text-red-500">Delete</button> */}
            {showModal ? <Modal id={blog._id} deletePost={deletePost}/> : null}

        </div>

      </div>
      ))
    }

  </div>
   )
 }

 export default ListOfPost
