import React from 'react';
import { Link } from 'react-router-dom';
// import { TERipple } from 'tw-elements-react';

const PostCard = ({blog})=> {
  const {title, img, description, _id} = blog
  console.log(blog);
  return (
      <div
        className="m-2 block rounded-lg shadow-[0_2px_15px_-3px_rgba(0,0,0,0.07),0_10px_20px_-2px_rgba(0,0,0,0.04)] dark:bg-neutral-700">
        <a href="#!">
          <img
            className="rounded-t-lg"
            src={`http://localhost:8000/api/post/create/${img}`}
            alt="" />
        </a>
        <div className="p-6">
          <h5
            className="mb-2 text-xl font-medium leading-tight text-neutral-800 dark:text-neutral-50">
            {title}
          </h5>
          <p className="mb-4 text-base text-neutral-600 dark:text-neutral-200 text-justify">
           {description.slice(0, 200)}
          </p>
            <Link
            to={`/singlePost/${_id}`}
              type="button"
              className="bg-white hover:bg-gray-100 text-gray-800 font-semibold py-2 px-4 border border-gray-400 rounded shadow">
              Read More ...
            </Link>
        </div>
      </div>
  );
}
export default PostCard
